import { useEffect, useState} from 'react';
import { useAuth0 } from '@auth0/auth0-react';



import { useAuthError } from './AuthErrorHandler';

function App() {
  const { loginWithRedirect, logout, user, isAuthenticated, isLoading } = useAuth0();
  const authError = useAuthError(); 

  // Background image URL configurable via environment variable
  const backgroundImageUrl = process.env.REACT_APP_BG_IMAGE_URL ||
    'https://images.unsplash.com/photo-1511316695145-4992006ffddb?w=900&auto=format&fit=crop&q=60&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxzZWFyY2h8Mnx8Y3J1aXNlfGVufDB8fDB8fHww';


  if (isLoading) return <div>Loading...</div>;

  return (
    <div
      style={{
        backgroundImage: `url(${backgroundImageUrl})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        minHeight: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        padding: '1rem',
      }}
    >
      <div
        style={{
          background: 'rgba(255, 255, 255, 0.85)',
          padding: '2rem',
          borderRadius: '8px',
          boxShadow: '0 4px 12px rgba(0, 0, 0, 0.15)',
          maxWidth: '400px',
          width: '100%',
          textAlign: 'center',
        }}
      >
        <h1 style={{ marginBottom: '1rem' }}>Welcome to My App</h1>

        {authError && (
          <div
            style={{
              background: '#fdecea',
              color: '#b71c1c',
              padding: '0.75rem 1rem',
              borderRadius: '4px',
              marginBottom: '1rem',
              fontSize: '0.9rem',
            }}
          >
            <strong>Error:</strong> {authError.description}
          </div>
        )}

        {!isAuthenticated ? (
          <button
            onClick={() => loginWithRedirect()}
            style={{
              background: '#1976d2',
              color: '#fff',
              border: 'none',
              padding: '0.75rem 1.5rem',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '1rem',
            }}
          >
            Log In
          </button>
        ) : (
          <>
            <h3 style={{ margin: '1rem 0' }}>Hello, {user.name}</h3>
            <button
              onClick={() => logout({ logoutParams: { returnTo: window.location.origin } })}
              style={{
                background: '#e53935',
                color: '#fff',
                border: 'none',
                padding: '0.75rem 1.5rem',
                borderRadius: '4px',
                cursor: 'pointer',
                fontSize: '1rem',
              }}
            >
              Log Out
            </button>
          </>
        )}
      </div>
    </div>
  );
}

export default App;
